//Постоянные для работы с постами
const UPDATE_TEXT_POST = 'UPDATE-TEXT-POST';
const ADD_POST = 'ADD-POST'
//Постоянные для работы с диалогами
const SELECT_DIALOG = 'SELECT-DIALOG';
const UPDATE_DIALOG_MESSAGE = 'UPDATE-DIALOG-MESSAGE';
const ADD_DIALOG_MESSAGE = 'ADD-DIALOG-MESSAGE';



let store = {
    _state:  {

        // Создадим объекты в котором будут хранится данные для каждой страницы
        profilePage: {
            // Массив в котором хранятся данные для постов
            postData: [
                { id: 1, message: 'Это первый пост', likesCount: '0' },
                { id: 2, message: 'Это второй пост', likesCount: '7' },
                { id: 5, message: 'Это третий пост', likesCount: '7' },
                { id: 12, message: 'Это четвертый пост', likesCount: '7' },
                { id: 10, message: 'Это пятый пост', likesCount: '7' },
            ],
    
            postText: ''
    
        },
    
        dialogsPage: {
            // Массив в котором хранятся данные Пользователей для списка диалогов
            dialogsData: [
                {
                    id: 1,
                    name: 'User1',
                    avatar: 'data:image/png;base64,',
                    messagesUserData: [
                        { id: 1, message: 'Message 1' },
                        { id: 2, message: 'Message 2' },
                        { id: 3, message: 'Message 3' },
                        { id: 4, message: 'Message 4' },
                        { id: 5, message: 'Message 5' }
                    ]
                },
                {
                    id: 2,
                    name: 'User2',
                    avatar: 'data:image/jpeg;base64,',
                    messagesUserData: [
                        { id: 6, message: 'Message 6' },
                        { id: 7, message: 'Message 7' },
                        { id: 8, message: 'Message 8' },
                        { id: 9, message: 'Message 9' },
                        { id: 10, message: 'Message 10' }
                    ]
                },
                {
                    id: 3,
                    name: 'User3',
                    avatar: 'data:image/jpeg;base64,',
                    messagesUserData: [
                        { id: 11, message: 'Message 11' },
                        { id: 12, message: 'Message 12' },
                        { id: 13, message: 'Message 13' },
                        { id: 14, message: 'Message 14' },
                        { id: 15, message: 'Message 15' }
                    ]
                },
                {
                    id: 40,
                    name: 'User4',
                    avatar: 'data:image/png;base64,',
                    messagesUserData: [
                        { id: 16, message: 'Message 16' },
                        { id: 27, message: 'Message 27' },
                        { id: 38, message: 'Message 38' },
                        { id: 49, message: 'Message 49' },
                        { id: 50, message: 'Message 50' }
                    ]
                }
            ],
            // Отрисовка сообщений для каждого пользователя и заголовок по умолчанию если диалогов нет
            dialogRenderData: {
                id: 1,
                name: 'User1',
                avatar: '',
                messagesUserData: [
                    { id: 1, message: 'Выберете пользователя что бы начать переписку' },
    
                ]
            },
    
            dialogNewText: ''
        },
        // Массив в котором хранятся данные для отображения страницы друзей
        friendsPage: {
            friendsData: [
                { id: 1, name: 'User1', avatar: 'data:image/png;base64,' },
                { id: 2, name: 'User2', avatar: 'data:image/jpeg;base64,' },
                { id: 3, name: 'User3', avatar: 'data:image/jpeg;base64,' },
                { id: 4, name: 'User4', avatar: 'data:image/png;base64,' },
                { id: 5, name: 'User5', avatar: 'data:image/png;base64,' },
                { id: 6, name: 'User6', avatar: 'data:image/jpeg;base64,' },
                { id: 7, name: 'User7', avatar: 'data:image/jpeg;base64,' },
                { id: 8, name: 'User8', avatar: 'data:image/png;base64,' }
            ]
        },
    
    },
    // метод отрисовки при изменении состояния
    _callSubscriber() {
    },

    // Создаем метод, который возвращает нам state, так как он приватный и на прямую мы к нему обращаться не должны ( можем но не будем)
    getState() {
        return this._state
    },
     // запуск ререндера при изменении this._state
    subscride(observer) {
        this._callSubscriber = observer;
    },

    // функция нахождения и присвоения нового id  используется только внутри store
    _newId(arr){
    let workArr = arr;
    if (workArr.length === 0) {
        let idMax = 1;
        return idMax
    } else {
        let idMax = Math.max(...workArr.map(i => i.id));
        idMax = idMax + 1;
        return idMax
    }
    },

    //  Можно сохранить функции и передавать в dispatch только имя.
    // А можно целиком перенести логику в dispatch
    // Сделал первую функцию с именем, а остальные перенес.
    _sidebarFriends() {
        let workArr = this._state.friendsPage.friendsData;
        let copyArr = workArr.map(el => el);
        let newFriendArr = []
    
        do {
            let randomIndex = Math.floor(Math.random() * copyArr.length);
            let randomElement = copyArr[randomIndex];
            if (randomElement !== undefined) {
                newFriendArr.push(randomElement);
                delete copyArr[randomIndex];
            }
        } while (newFriendArr.length < 3)
    
        return newFriendArr
    },

    dispatch(action) {
        if (action.type === 'SIDEBAR-FRIENDS' ) {
           return this._sidebarFriends();
        } else if (action.type === 'UPDATE-TEXT-POST') {
            this._state.profilePage.postText = action.postText;
            this._callSubscriber();
        } else if (action.type === 'ADD-POST') {
            let postObj = {
                id: this._newId(this._state.profilePage.postData),
                message: this._state.profilePage.postText,
                likesCount: 0
            }
    
            this._state.profilePage.postData.push(postObj);
            this._state.profilePage.postText = '';
            this._callSubscriber(this._state);
        } else if (action.type === 'SELECT-DIALOG') {

            this._state.dialogsPage.dialogsData.forEach(element => {
                if (element.id === action.userId) {
                    let indexEl = this._state.dialogsPage.dialogsData.indexOf(element);
                    this._state.dialogsPage.dialogRenderData = this._state.dialogsPage.dialogsData[indexEl];
                }
                this._callSubscriber();
            })
        } else if (action.type === 'UPDATE-DIALOG-MESSAGE') {
            this._state.dialogsPage.dialogNewText = action.messageText;
            this._callSubscriber();
        } else if ( action.type === 'ADD-DIALOG-MESSAGE') {
            let userId = this._state.dialogsPage.dialogRenderData.id
            let messageObj = {
                id: this._newId(this._state.dialogsPage.dialogRenderData.messagesUserData),
                message: this._state.dialogsPage.dialogNewText,
            }
        
            this._state.dialogsPage.dialogsData.forEach(element => {
                if (element.id === userId) {
                    element.messagesUserData.push(messageObj)
                }
            })
            this._state.dialogsPage.dialogNewText = '';
            this._callSubscriber();
        
        }
    } 

}

// Добавление постов
export const actionCreaterOnPostUpdate = (newPostText) => ({type: UPDATE_TEXT_POST, postText: newPostText});
export const actionCreaterAddPost = () => ({type: ADD_POST});

// Работа с диалогами - выбор диалога, обновление ввода сообщения, добавление сообщения
export const actionCreaterSelectDialog = (userId) => ({type: SELECT_DIALOG , userId: userId})
export const actionCreaterUpdateTextMessage = (newTextMessage) => ({type: UPDATE_DIALOG_MESSAGE, messageText: newTextMessage})
export const actionCreaterAddMessage = () => ({type: ADD_DIALOG_MESSAGE})

export default store;

